mapper.insert:

insert into tb_moved (id, user_id, moved_time, 
  moved_place_x, moved_place_y, heart_rate
  )
values (#{id,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, #{movedTime,jdbcType=TIMESTAMP}, 
  #{movedPlaceX,jdbcType=REAL}, #{movedPlaceY,jdbcType=REAL}, #{heartRate,jdbcType=REAL}
  )

mapper.select:
  	SELECT MAX(a.id),a.user_id as userId,b.name,b.birthday as birthDay,b.phone_number as phoneNum,b.sex,a.heart_rate as heartRate,a.moved_place_x as localX,a.moved_place_y as localY
	FROM tb_moved a 
	JOIN tb_user b ON a.user_id=b.id
	WHERE a.moved_time BETWEEN #{movedTimeMin} AND #{movedTimeMax} AND a.moved_place_x BETWEEN #{movedPlaceXMin} AND #{movedPlaceXMax} AND a.moved_place_y BETWEEN #{movedPlaceYMin} AND #{movedPlaceYMax} AND a.user_id != #{userId}
	GROUP BY a.user_id;
